static int yy_init_globals (void)
{
        /* Initialization is the same as for the non-reentrant scanner.
     * This function is called from xylex_destroy(), so don't allocate here.
     */

    (yy_buffer_stack) = 0;
    (yy_buffer_stack_top) = 0;
    (yy_buffer_stack_max) = 0;
    (yy_c_buf_p) = (char *) 0;
    (yy_init) = 0;
    (yy_start) = 0;

/* Defined in main.c */
#ifdef YY_STDINIT
    xyin = stdin;
    xyout = stdout;
#else
    xyin = (FILE *) 0;
    xyout = (FILE *) 0;
#endif

    /* For future reference: Set errno on error, since we are called by
     * xylex_init()
     */
    return 0;
}

/* xylex_destroy is for both reentrant and non-reentrant scanners. */
int xylex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		xy_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		xypop_buffer_state();
	}

	/* Destroy the stack itself. */
	xyfree((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * xylex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

